alter PROCEDURE [dbo].[REPORTE_NIÑO_2022]
	@mes integer,   
    @anio integer       
AS   
BEGIN    

		



		IF OBJECT_ID ( 'dbo.nino_tmp','U' ) IS NULL             
		CREATE TABLE nino_tmp(                 
			[actividad] [nvarchar](500) NOT NULL,
			[cod_estab] [nvarchar](20) NOT NULL,
			[descripcion] [varchar](200) NOT NULL,
			[cantidad] [int] NOT NULL          
		) ON [PRIMARY];




		IF OBJECT_ID ( 'dbo.atenciones_ninio','U' ) IS NULL             
		SELECT *
		INTO atenciones_ninio
		FROM BDHIS_MINSA.DBO.rpt_plano_cbeta_acum
		WHERE anio = @anio AND mes = @mes AND
			  ((edad_reg BETWEEN 0 AND 30 AND tipo_edad = 'D') OR
			   (edad_reg BETWEEN 1 AND 11 AND tipo_edad = 'M') OR
			   (edad_reg BETWEEN 1 AND 11 AND tipo_edad = 'A')
			  ) and codigo_unico is not null;
		             
		DELETE FROM nino_tmp;


			select * into #DX_ANEMIA from atenciones_ninio h1
			where h1.codigo_item in ('D500','D508','D509','D649')
			AND h1.tipo_diagnostico in ('D','R')

			select * INTO #BAJO_PESO_PREMATURO from atenciones_ninio
			where Codigo_Item IN('P073','P070','P071')

			---Temporal de IRAS EDAS
			SELECT  TOP(1)*,'3331101' PRODUCTO, 'Infección respiratoria aguda (IRA) no complicada                                                                       ' DESCRIPCION_PRODUCTO ,
CASE WHEN Edad_Reg BETWEEN 0 AND 29 AND Tipo_Edad='D'  THEN '0 A 29 DIAS'WHEN (Edad_Reg BETWEEN 0 AND 29 AND Tipo_Edad='D') OR (Tipo_Edad='M' AND Edad_Reg=1 )   THEN '29 A 59 DIAS' WHEN (Tipo_Edad='M' AND Edad_Reg BETWEEN 2 AND 11 )   THEN '2 A 11 MESES' WHEN (Tipo_Edad='A' AND Edad_Reg BETWEEN 1 AND 4 )   THEN '1 A 4 AÑOS' WHEN (Tipo_Edad='A' AND Edad_Reg BETWEEN 5 AND 11 )   THEN '5 A 11 AÑOS'END  GRUPO_EDAD
 INTO #IRAS_EDAS
 FROM rpt_plano_cbeta_acum 
WHERE 1=2

			INSERT INTO #IRAS_EDAS SELECT  *,'3331101' PRODUCTO, 'Infección respiratoria aguda (IRA) no complicada' DESCRIPCION_PRODUCTO ,
CASE WHEN Edad_Reg BETWEEN 0 AND 29 AND Tipo_Edad='D'  THEN '0 A 29 DIAS'WHEN (Edad_Reg BETWEEN 0 AND 29 AND Tipo_Edad='D') OR (Tipo_Edad='M' AND Edad_Reg=1 )   THEN '29 A 59 DIAS' WHEN (Tipo_Edad='M' AND Edad_Reg BETWEEN 2 AND 11 )   THEN '2 A 11 MESES' WHEN (Tipo_Edad='A' AND Edad_Reg BETWEEN 1 AND 4 )   THEN '1 A 4 AÑOS' WHEN (Tipo_Edad='A' AND Edad_Reg BETWEEN 5 AND 11 )   THEN '5 A 11 AÑOS'END  GRUPO_EDAD
 FROM rpt_plano_cbeta_acum 
WHERE Codigo_Item in ('J00X','J040', 'J041', 'J042', 'J060', 'J068', 'J069', 'J209')
and Anio_Actual_Paciente<=11 and @anio=2022 and @mes=03 

