	--=====================================================================================================================
	------- INICIO DE CALCULO DE VARIABLES --------------------------------------------------------------------------------
	--=====================================================================================================================

		--I. ATENCION DEL RECIEN NACIDO          
		--A) Atencion Inmediata          
		--ACTIVIDADES       
		--Atencion Inmediata       
		insert into nino_tmp              
		select '001. I. ATENCION DEL RECIEN NACIDO - A) Atencion Inmediata - ACTIVIDADES - Atencion Inmediata' Actividad,              
			h.codigo_unico,              
			h.nombre_establecimiento,			              
			count(*) Cantidad          
		from 	atenciones_ninio as h
		where        
			rtrim(h.codigo_item)in('99436')  ---VALIDAR 99460     
			and rtrim(h.tipo_diagnostico) in ('D')       
	--		and rtrim(h.valor_lab) in ('','RN')       
			and h.edad_reg between 1 and 1      
			and h.tipo_edad = 'D'       
			and h.mes=@mes       
			and h.anio=@anio       
		group by h.codigo_unico,              
		h.nombre_establecimiento;       
		--I. ATENCION DEL RECIEN NACIDO          
		--A) Atencion Inmediata          
		--ACTIVIDADES       
		--Tamizaje Neonatal (Enfermed. Metab?licas)          
		insert into nino_tmp              
		select '002. I. ATENCION DEL RECIEN NACIDO - A) Atencion Inmediata - ACTIVIDADES - Tamizaje Neonatal (Enfermed. Metab?licas)' Actividad,              
			h.codigo_unico,              
			h.nombre_establecimiento,			              
			count(*) Cantidad              
		from 	atenciones_ninio as h          
			          
		where           
			rtrim(h.codigo_item)in('36416')          
			and rtrim(h.tipo_diagnostico) in ('D')          
			and rtrim(h.valor_lab) in ('1')  --se cambio licenciada rosa-red cajamarca        
			and h.edad_reg between 3 and 6          
			and h.tipo_edad = 'D'          
			and h.mes=@mes              
			and h.anio=@anio              
		group by h.codigo_unico,          
		h.nombre_establecimiento;          
		--I. ATENCION DEL RECIEN NACIDO          
		--A) Atencion Inmediata          
		--ACTIVIDADES          
		--Tamizaje  Auditivo          
		insert into nino_tmp              
		select '007. I. ATENCION DEL RECIEN NACIDO - A) Atencion Inmediata - ACTIVIDADES - Tamizaje Visual y Auditivo' Actividad,              
			h.codigo_unico,              
			h.nombre_establecimiento,			              
			count(*) Cantidad   --No se esta realizando las actividades            
		from 	atenciones_ninio as h      
			          
		where           
			rtrim(h.codigo_item) in('99431.01')          
			and rtrim(h.tipo_diagnostico) in ('D')          
			and rtrim(h.valor_lab) in ('1')          
			and h.edad_reg between 1 and 28   
			and h.tipo_edad='D'
			         
			and h.mes=@mes              
			and h.anio=@anio              
