		where           
			rtrim(h.codigo_item) in('99431.02')          
			and rtrim(h.tipo_diagnostico) in ('D')       
			          
			and h.edad_reg between 1 and 28   
			and h.tipo_edad='D'			         
			and h.mes=@mes              
			and h.anio=@anio              
