		where        
			 
			(  RTRIM(h.codigo_item )IN ('99436.02'))and
			rtrim(h.tipo_diagnostico) in ('D')
			and h.edad_reg between 1 and 1          
			and h.tipo_edad = 'D' 		   
			and h.mes=@mes       
