		from 	atenciones_ninio as h       
			       
		where        
			(rtrim(h.codigo_item) in ('99460')  )         
			and h.edad_reg between 1 and 1          
			and h.tipo_edad = 'D'			    
			and h.mes=@mes       
			and h.anio=@anio              
